        public async Task SendInvoiceEmailAsync(string toEmail, string subject, string htmlContent)
        {
            // Cấu hình SMTP server
            using (SmtpClient smtpClient = new SmtpClient("smtp.gmail.com", 587))
            {
                smtpClient.EnableSsl = true;
                smtpClient.Credentials = new NetworkCredential("tiep.nguyentonyfake@hcmut.edu.vn", "zrddeegmguihuuor");

                // Tạo mail message
                using (MailMessage mail = new MailMessage())
                {
                    mail.From = new MailAddress("tiep.nguyentonyfake@hcmut.edu.vn");
                    mail.To.Add(toEmail);
                    mail.Subject = subject;
                    mail.Body = htmlContent;
                    mail.IsBodyHtml = true; // Quan trọng nếu bạn gửi HTML

                    // Gửi email bất đồng bộ
                    try
                    {
                        await smtpClient.SendMailAsync(mail);
                    }
                    catch (Exception ex)
                    {
                        // Xử lý lỗi ở đây, có thể log lỗi hoặc thông báo cho người dùng
                        Console.WriteLine("Unable to send email. Error: " + ex.Message);
                    }
                }
            }
        }

        public string FormatOrderId(int orderId)
        {
            // Chuyển orderId thành chuỗi và đảo ngược
            string reversedOrderId = (string)orderId.ToString().Reverse();

            // Lấy 5 chữ số từ cuối tới đầu
            string firstFiveDigits = string.Concat(reversedOrderId.Take(5));

            // Đảo ngược lại để đưa về đúng thứ tự ban đầu
            string formattedOrderId = string.Concat(firstFiveDigits.Reverse());

            // Thêm số 0 vào đầu cho đến khi đủ 7 số
            while (formattedOrderId.Length < 7)
            {
                formattedOrderId = "0" + formattedOrderId;
            }

            return formattedOrderId;
        }
    }
