    [Authorize(Roles = SystemDefinitions.Role_Customer)]
    public class PaymentController : Controller
    {
        private readonly ILogger<PaymentController> _logger;
        private readonly ApplicationDbContext _context;
        private readonly IVnPayService _vnPayService;
        private readonly UserManager<ApplicationUser> _userManager;

        public PaymentController(ILogger<PaymentController> logger, ApplicationDbContext context, IVnPayService vnPayService, UserManager<ApplicationUser> userManager)
        {
            _context = context;
            _logger = logger;
            _vnPayService = vnPayService;
            _userManager = userManager;
        }

        // GET: /<controller>/
        public IActionResult Index()
        {
            return View();
        }

        [HttpPost]
        public async Task<IActionResult> checkout([FromBody] PaymentModel model)
        {
            var selectedProducts = HttpContext.Session.GetObjectFromJson<List<ProductItem>>("SelectedProducts");
            var user = await _userManager.GetUserAsync(User);

            //Kiem tra so luong ton kho va so luong dat truoc khi thanh toan
            bool duhang = true;
            foreach (var item in selectedProducts) // Sửa tại đây
            {
                var productInDb = await _context.Products
                    .Where(p => p.Id == item.ProductId)
                    .FirstOrDefaultAsync();

                if (productInDb == null || productInDb.StockQuantity < item.Quantity)
                {
                    duhang = false;
                    break;
                }
            }


            //Tinh tong so tien can thanh toan dua vao vnpay
            double totalSum = 0;
            int totalAmount = 0;

            if (selectedProducts != null)
            {
                foreach (var item in selectedProducts)
                {
                    totalAmount += item.Quantity;
                    // Sử dụng Regex để loại bỏ các ký tự không mong muốn
                    var cleanedTotalPrice = Regex.Replace(item.TotalPrice, @"đ|,|\.|\s", string.Empty);

                    // Chuyển đổi sang double và cộng dồn vào totalSum
                    if (double.TryParse(cleanedTotalPrice, System.Globalization.NumberStyles.Any, System.Globalization.CultureInfo.InvariantCulture, out var totalPrice))
                    {
                        totalSum += totalPrice;
                    }
                }
                totalSum += 50000;
            }

            //Kiểm tra đủ hàng trước khi thanh toán tiền mặt
            if (model.Payment == "Tien Mat" && duhang)
            {
                var order = new Order
                {
                    UserId = user.Id,
                    IsDeleted = false,
                    OrderDate = DateTime.Now,
                    Status = "Pending",
                    TotalAmount = (decimal)totalSum
                };
